var dataForWeixin = {
        appId : "wxd588ca9e0a8b634e",
        MsgImg : "http://www.qiyipic.com/common/fix/xinlizui_share_cover.jpg",
        TLImg : "http://www.qiyipic.com/common/fix/xinlizui_share_cover.jpg",
        url : location.href,
        title : "快来人呐！又死人啦！！！",
        desc : " C市已经连续发生4起强奸杀人案，下一个被害者会是谁？人心惶惶，等你破案！",
        fakeid : "",
        callback : function() {
        }
};
(function() {
    
    var isIphone = function() {
        var ua = window.navigator.userAgent;
         return /iPhone OS/.test(ua);
    };
    
    if(isIphone()){
        var imgBase64 = "data:image/jpg;base64,";
        dataForWeixin.MsgImg = imgBase64;
        dataForWeixin.TLImg = imgBase64;
    }
    
    var onBridgeReady = function() {
        WeixinJSBridge.on('menu:share:appmessage', function(argv) {
            WeixinJSBridge.invoke('sendAppMessage', {
                "appid" : dataForWeixin.appId,
                "img_url" : dataForWeixin.MsgImg,
                "img_width" : "120",
                "img_height" : "120",
                "link" : dataForWeixin.url,
                "desc" : dataForWeixin.desc,
                "title" : dataForWeixin.title
            }, function(res) {
                (dataForWeixin.callback)();
            });
        });
        WeixinJSBridge.on('menu:share:timeline', function(argv) {
            (dataForWeixin.callback)();
            WeixinJSBridge.invoke('shareTimeline', {
                "img_url" : dataForWeixin.TLImg,
                "img_width" : "120",
                "img_height" : "120",
                "link" : dataForWeixin.url,
                "desc" : dataForWeixin.desc,
                "title" : dataForWeixin.title
            }, function(res) {
            });
        });
        WeixinJSBridge.on('menu:share:weibo', function(argv) {
            WeixinJSBridge.invoke('shareWeibo', {
                "content" : dataForWeixin.title,
                "url" : dataForWeixin.url
            }, function(res) {
                (dataForWeixin.callback)();
            });
        });
        WeixinJSBridge.on('menu:share:facebook', function(argv) {
            (dataForWeixin.callback)();
            WeixinJSBridge.invoke('shareFB', {
                "img_url" : dataForWeixin.TLImg,
                "img_width" : "120",
                "img_height" : "120",
                "link" : dataForWeixin.url,
                "desc" : dataForWeixin.desc,
                "title" : dataForWeixin.title
            }, function(res) {
            });
        });
    };
    if (document.addEventListener) {
        document.addEventListener('WeixinJSBridgeReady', onBridgeReady, false);
    } else if (document.attachEvent) {
        document.attachEvent('WeixinJSBridgeReady', onBridgeReady);
        document.attachEvent('onWeixinJSBridgeReady', onBridgeReady);
    }
})(); 